import "boxicons";
import React, { useEffect, useRef, useState } from "react";

const Player = () => {
  const [position, setPosition] = useState({x: 0, y: 0})
  const person = useRef()
  const block = useRef()

  useEffect(() => {
    person.current.style.left = `${position.x+window.scrollX-40}px`;
    person.current.style.top = `${position.y+window.scrollY-40}px`;
  
    person.current.offsetBottom = person.current.offsetTop + person.current.offsetHeight;
    person.current.offsetRight = person.current.offsetLeft + person.current.offsetWidth;
    block.current.offsetBottom = block.current.offsetTop + block.current.offsetHeight;
    block.current.offsetRight = block.current.offsetLeft + block.current.offsetWidth;

    let result = !((person.offsetBottom < block.offsetTop) ||
             (person.offsetTop > block.offsetBottom) ||
             (person.offsetRight < block.offsetLeft) ||
             (person.offsetLeft > block.offsetRight))

    console.log(result)

  }, [position])

  const updatePosition = (e) => {
    
    setPosition({x: e.clientX, y: e.clientY });
    console.log(position)
  }

  useEffect(() => {
    const handleMouseMove = (e) => {
      // Получаем текущие позиции мыши и размеры окна
      const mouseX = e.clientX;
      const mouseY = e.clientY;
      const windowWidth = window.innerWidth;
      const windowHeight = window.innerHeight;

      // Задаем порог для прокрутки
      const threshold = 200;
      console.log(position)
      // Проверяем, если курсор мыши приблизился к границам окна, прокручиваем страницу
      if (mouseX < threshold) {
        window.scrollBy(-2, 0);
        
         // Прокручиваем влево
      } else if (mouseX > windowWidth - threshold) {
        window.scrollBy(2, 0); // Прокручиваем вправо
        

      }

      if (mouseY < threshold) {
        window.scrollBy(0, -2); // Прокручиваем вверх
      } else if (mouseY > windowHeight - threshold) {
        window.scrollBy(0, 2); // Прокручиваем вниз
      }
    };

    document.addEventListener('mousemove', handleMouseMove);

   

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);



  return (
    <div className="w-full h-full bg-slate-800 bg" onMouseMove={updatePosition}>
      <div className="person w-20 h-20 absolute top-0 left-0 z-10" ref={person}></div>


      <div className="h-full bg-blue-900 w-36 absolute" ref={block}></div>
      <div className="w-full bg-blue-900 h-36 absolute"></div>
      <div className="w-full bg-blue-900 h-10 absolute bottom-0"></div>
      <div className=" bg-blue-900 w-56 absolute left-64 h-5/6"></div>
      <div className=" bg-blue-900 w-36 absolute left-1/3 h-4/6 bottom-0"></div>
      <div className=" bg-blue-900 w-36 absolute left-1/2 h-3/6 bottom-0"></div>
      <div className=" bg-blue-900 w-36 absolute left-1/2 h-3/6 -top-24"></div>
      <div className=" bg-blue-900 w-36 absolute left-2/3 h-48 bottom-0"></div>
      <div className=" bg-blue-900 w-36 absolute left-2/3 h-2/3 top-46"></div>
      <div className=" bg-blue-900 w-20 absolute right-48 h-2/3 top-46"></div>
      <div className=" bg-blue-900 w-20 absolute right-48 h-48 bottom-0"></div>
      <div className=" bg-blue-900 w-28 absolute right-0 h-full bottom-0"></div>
      <div className=" bg-red-900 w-10 absolute right-32 h-10 top-48"></div>
      
    </div>
  );
};

export default Player;
