import "boxicons";
import React, { useEffect, useRef, useState } from "react";

const Player = () => {
  const [position, setPosition] = useState({x: 0, y: 0})
  const person = useRef()

  useEffect(() => {
    person.current.style.left = `${position.x-40}px`;
    person.current.style.top = `${position.y-40}px`;
    console.log(position)
  }, [position])

  const updatePosition = (e) => {
    setPosition({x: e.clientX, y: e.clientY });
  }



  return (
    <div className="w-full h-full" onMouseMove={updatePosition}>
      <div className="person w-20 h-20 absolute top-0 left-0" ref={person}></div>


      <div className="h-full bg-blue-900 w-36 absolute"></div>
      <div className="w-full bg-blue-900 h-36 absolute"></div>
      <div className="h-full bg-blue-900 w-36 absolute left-10"></div>
      
    </div>
  );
};

export default Player;
