import "boxicons";
import React, { useEffect, useRef, useState } from "react";

const Player = () => {
  const [position, setPosition] = useState({x: 0, y: 0})
  const person = useRef()
  const block = useRef()

  useEffect(() => {
    person.current.style.left = `${position.x-40}px`;
    person.current.style.top = `${position.y-40}px`;
    


  }, [position])

  const updatePosition = (e) => {
    setPosition({x: e.clientX, y: e.clientY });
  }



  return (
    <div className="w-full h-full bg-slate-800" onMouseMove={updatePosition}>
      <div className="person w-20 h-20 absolute top-0 left-0 z-10" ref={person}></div>


      <div className="h-full bg-blue-900 w-36 absolute" ref={block}></div>
      <div className="w-full bg-blue-900 h-36 absolute"></div>
      <div className="w-full bg-blue-900 h-10 absolute bottom-0"></div>
      <div className=" bg-blue-900 w-56 absolute left-64 h-5/6"></div>
      <div className=" bg-blue-900 w-36 absolute left-1/3 h-4/6 bottom-0"></div>
      <div className=" bg-blue-900 w-36 absolute left-1/2 h-3/6 bottom-0"></div>
      <div className=" bg-blue-900 w-36 absolute left-1/2 h-3/6 -top-24"></div>
      <div className=" bg-blue-900 w-36 absolute left-2/3 h-48 bottom-0"></div>
      <div className=" bg-blue-900 w-36 absolute left-2/3 h-2/3 top-46"></div>
      <div className=" bg-blue-900 w-20 absolute right-48 h-2/3 top-46"></div>
      <div className=" bg-blue-900 w-20 absolute right-48 h-48 bottom-0"></div>
      <div className=" bg-blue-900 w-28 absolute right-0 h-full bottom-0"></div>
      <div className=" bg-red-900 w-10 absolute right-32 h-10 top-48"></div>
      
    </div>
  );
};

export default Player;
